import React from 'react';
import { Button, Card } from 'react-bootstrap';

const InfoSection = ({ location = 'гр.Варна', workingHours = 'От Пон. до Петък/12:00-20:00', confirmationTime = 'До 24 часа' }) => {
  return (
    <Card className="d-flex flex-row align-items-center p-3" style={{ borderRadius: '12px', gap: '50px', backgroundColor: '#fff' }}>
      {/* Location Section */}
      <div className="d-flex align-items-center" style={{ flex: 1 }}>
        <img src="https://dashboard.codeparrot.ai/api/image/Z7T-6qWN819FoZop/vuesax-l.png" alt="location" width="32" height="32" />
        <div className="ms-3">
          <div style={{ fontFamily: 'Poppins', fontSize: '16px', fontWeight: 500, color: '#3e3e3e' }}>
            Местоположение
          </div>
          <div style={{ fontFamily: 'Poppins', fontSize: '14px', color: '#b6b6b6' }}>
            {location}
          </div>
        </div>
      </div>

      {/* Working Hours Section */}
      <div className="d-flex align-items-center" style={{ flex: 1, borderLeft: '1px solid #acacac', paddingLeft: '24px' }}>
        <img src="https://dashboard.codeparrot.ai/api/image/Z7T-6qWN819FoZop/vuesax-l-2.png" alt="calendar" width="32" height="32" />
        <div className="ms-3">
          <div style={{ fontFamily: 'Poppins', fontSize: '16px', fontWeight: 500, color: '#3e3e3e' }}>
            Работно време
          </div>
          <div style={{ fontFamily: 'Poppins', fontSize: '14px', color: '#b6b6b6' }}>
            {workingHours}
          </div>
        </div>
      </div>

      {/* Confirmation Section */}
      <div className="d-flex align-items-center" style={{ flex: 1, borderLeft: '1px solid #acacac', paddingLeft: '24px' }}>
        <img src="https://dashboard.codeparrot.ai/api/image/Z7T-6qWN819FoZop/vuesax-b.png" alt="call" width="35" height="32" />
        <div className="ms-3">
          <div style={{ fontFamily: 'Poppins', fontSize: '16px', fontWeight: 500, color: '#3e3e3e' }}>
            Потвърждения
          </div>
          <div style={{ fontFamily: 'Poppins', fontSize: '14px', color: '#b6b6b6' }}>
            {confirmationTime}
          </div>
        </div>
      </div>

      {/* Learn More Button */}
      <Button 
        className="d-flex align-items-center justify-content-center"
        style={{
          backgroundColor: '#1572d3',
          border: 'none',
          borderRadius: '8px',
          padding: '8px 15px',
          fontFamily: 'Poppins',
          fontSize: '16px',
          fontWeight: 700,
          whiteSpace: 'nowrap',
          color: '#fff'
        }}
        onMouseOver={(e) => e.target.style.backgroundColor = '#1260b3'}
        onMouseOut={(e) => e.target.style.backgroundColor = '#1572d3'}
      >
        НАУЧИ ПОВЕЧЕ
      </Button>
    </Card>
  );
};

export default InfoSection;
