import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function BookingForm() {
  const { date } = useParams(); // Get the selected date from URL params
  const [bookingData, setBookingData] = useState({
    name: '',
    email: '',
    phone: '',
    bookingDate: date,
  });

  useEffect(() => {
    // Set the default booking date to the one passed in the URL
    setBookingData((prevData) => ({ ...prevData, bookingDate: date }));
  }, [date]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setBookingData({
      ...bookingData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert('Booking confirmed! \n' + JSON.stringify(bookingData, null, 2));
    // You can send the data to an API here.
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">Booking Form for {bookingData.bookingDate}</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Name</label>
          <input
            type="text"
            name="name"
            value={bookingData.name}
            onChange={handleInputChange}
            className="form-control"
            required
          />
        </div>
        <div className="form-group">
          <label>Email</label>
          <input
            type="email"
            name="email"
            value={bookingData.email}
            onChange={handleInputChange}
            className="form-control"
            required
          />
        </div>
        <div className="form-group">
          <label>Phone</label>
          <input
            type="text"
            name="phone"
            value={bookingData.phone}
            onChange={handleInputChange}
            className="form-control"
            required
          />
        </div>
        <button type="submit" className="btn btn-primary mt-3">
          Confirm Booking
        </button>
      </form>
    </div>
  );
}

export default BookingForm;
