import React, { useState, useEffect, useMemo } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import 'bootstrap/dist/css/bootstrap.min.css';

const localizer = momentLocalizer(moment);

function App() {
  const [bookedDates, setBookedDates] = useState([]);

  // Simulated API call - replace with real API
  useEffect(() => {
    const mockBookedDates = ['2025-02-15', '2025-02-20', '2025-06-01', '2025-06-10'];
    setBookedDates(mockBookedDates);
  }, []);

  // Memoize booked dates for performance
  const bookedDatesSet = useMemo(() => new Set(bookedDates), [bookedDates]);

  // Style booked dates differently
  const dayPropGetter = (date) => {
    const dateStr = moment(date).format('YYYY-MM-DD');
    const isBooked = bookedDatesSet.has(dateStr);

    return {
      className: `text-white ${isBooked ? 'bg-danger' : 'bg-success'}`,
      style: {
        cursor: isBooked ? 'not-allowed' : 'pointer',
        transition: 'background 0.3s ease',
      },
    };
  };

  // Handle user selecting a date
  const handleSelectSlot = ({ start }) => {
    const dateStr = moment(start).format('YYYY-MM-DD');

    if (bookedDatesSet.has(dateStr)) {
      alert(`Date ${dateStr} is already booked!`);
      return;
    }

    // Replace with real booking logic
    alert(`Booking request for ${dateStr}`);
  };

  return (
    <div className="App container mt-5">
      <header className="text-center mb-4">
        <h1>Booking Calendar</h1>
      </header>

      <div className="card shadow">
        <div className="card-body">
          <Calendar
            localizer={localizer}
            events={[]} 
            startAccessor="start"
            endAccessor="end"
            selectable
            onSelectSlot={handleSelectSlot}
            dayPropGetter={dayPropGetter}
            defaultView="month"
            views={['month']}
            style={{ height: '70vh', minHeight: '500px' }}
            className="border-0"
          />
        </div>
      </div>

