import React from "react";
import { Container } from "react-bootstrap";
import HeaderNavigation from "./HeaderNavigation";
import HeroSection from "./HeroSection";
import InfoSection from "./InfoSection";
import HowItWorks from "./HowItWorks";

const Layout = () => {
  return (
    <Container
      fluid
      className="d-flex flex-column min-vh-100"
      style={{
        background: "#0097", // Soft neutral background
        backgroundImage:
          "url('https://www.transparenttextures.com/patterns/real-carbon-fibre.png')", // Subtle texture
        backgroundAttachment: "fixed",
      }}
    >
      <HeaderNavigation />
      {/* Section one start*/}
      <div
        style={{
          background: "#ffffff",
          padding: "50px 15px",
          borderRadius: "12px",
          margin: "20px auto",
          marginTop: "100px",
          maxWidth: "1800px",
          boxShadow: "0px 4px 12px rgb(0, 0, 0)",
        }}
      >
        <HeroSection />
        <InfoSection />
      </div>
      {/* Section one end*/}
      {/* Section two start */}
      <div
        style={{
          background: "#ffffff",
          padding: "50px 15px",
          borderRadius: "12px",
          margin: "20px auto",
          marginTop: "80px",
          maxWidth: "1800px",
          width: "100%", // Forces it to take the full width like the first section
          boxShadow: "0px 4px 12px rgba(0, 0, 0, 0.74)",
        }}
      >
        <HowItWorks />
      </div>
      {/* Section two end */}
    </Container>
  );
};

export default Layout;
