import React from 'react';
import { Button } from 'react-bootstrap';

const HeroSection = ({
  title = 'Избери, резервирай и провери своят автомобил',
  subtitle = 'Най-сигурната проверка на автомобили!',
  buttonText = 'Запиши се'
}) => {
  return (
    <div className="container mt-5">
      <div className="row">
        <div className="col-12">
          <div className="d-flex flex-column align-items-start gap-4">
            {/* Main Title */}
            <h1 className="fw-semibold" style={{ 
              fontSize: '48px', 
              lineHeight: '100%',
              color: '#272727',
              maxWidth: '566px'
            }}>
              {title}{' '}
              <span style={{ 
                color: '#1572d3',
                position: 'relative',
                display: 'inline-block' 
              }}>
                Лесно
                <img 
                  src="https://dashboard.codeparrot.ai/api/image/Z7PAvjO_YEiK21yT/vector-1.png"
                  alt=""
                  style={{
                    position: 'absolute',
                    bottom: '-10px',
                    left: '0',
                    width: '134px',
                    height: '48px'
                  }}
                />
              </span>
            </h1>

            {/* Subtitle */}
            <p style={{ 
              fontSize: '18px',
              lineHeight: '150%',
              color: '#272727',
              maxWidth: '390px'
            }}>
              {subtitle}
            </p>

            {/* CTA Button */}
            <Button
              className="text-uppercase fw-bold"
              style={{
                backgroundColor: '#1572d3',
                border: 'none',
                width: '253px',
                height: '51px',
                padding: '8px',
                borderRadius: '8px',
                fontSize: '16px',
                lineHeight: '150%'
              }}
            >
              {buttonText}
            </Button>

            {/* Bottom Image */}
            <img 
              src="https://dashboard.codeparrot.ai/api/image/Z7PAvjO_YEiK21yT/frame-2.png"
              alt="Car illustration"
              style={{
                maxWidth: '259px',
                height: 'auto'
              }}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default HeroSection;
