import React from 'react';
import { Button, Card } from 'react-bootstrap';

const HowItWorks = () => {
  const steps = [
    {
      icon: "https://dashboard.codeparrot.ai/api/image/Z7egJ68BBwoP-o1m/vuesax-b.png",
      title: "Избери дата",
      description: "Избери най-удобният за теб\nден"
    },
    {
      icon: "https://dashboard.codeparrot.ai/api/image/Z7egJ68BBwoP-o1m/vuesax-b-2.png", 
      title: "Попълни информацията",
      description: "Изберете нужната за Вас услуга и попълнете всяко информационно поле"
    },
    {
      icon: "https://dashboard.codeparrot.ai/api/image/Z7egJ68BBwoP-o1m/vuesax-b-3.png",
      title: "Очаквайте ни!",
      description: "Паркитайте автомобилът на удобно за Вас място, ние ще Ви открием!"
    }
  ];

  return (
    <div className="container py-5">
      <div className="row justify-content-center text-center">
        <div className="col-12">
          <Button variant="light" className="mb-4" style={{backgroundColor: '#1572d31a', borderRadius: '8px'}}>
            <span className="text-primary fw-bold">КАК ПРОЦЕДИРАМЕ НИЕ</span>
          </Button>

          <h2 className="fw-semibold mb-5" style={{fontSize: '38px', fontFamily: 'Poppins', color: '#333'}}>
            Запази своят час точно в 3 стъпки
          </h2>

          <div className="row justify-content-center">
            {steps.map((step, index) => (
              <div key={index} className="col-12 col-md-4 mb-4">
                <Card className="text-center border-0">
                  <Card.Body>
                    <div className="bg-primary bg-opacity-10 rounded-3 p-4 mb-4" style={{width: '112px', height: '112px', margin: '0 auto'}}>
                      <img 
                        src={step.icon}
                        alt={step.title}
                        className="img-fluid"
                        style={{width: '48px', height: '48px'}}
                      />
                    </div>

                    <Card.Title className="fw-medium mb-3" style={{fontSize: '20px', fontFamily: 'Poppins', color: '#000'}}>
                      {step.title}
                    </Card.Title>

                    <Card.Text className="text-muted" style={{fontSize: '14px', fontFamily: 'Poppins'}}>
                      {step.description}
                    </Card.Text>
                  </Card.Body>
                </Card>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default HowItWorks;
