import React, { useState, useEffect, useMemo } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useNavigate } from 'react-router-dom';
import { Routes, Route } from 'react-router-dom';  
import { useParams } from 'react-router-dom';

const localizer = momentLocalizer(moment);

function App() {
  const [bookedEvents, setBookedEvents] = useState([]);
  const [currentDate, setCurrentDate] = useState(new Date());
  const navigate = useNavigate();

  // Simulated API call - Fetch booked events for the selected month
  useEffect(() => {
    const mockData = [
      { start: '2025-02-15T10:00:00', end: '2025-02-15T12:00:00' },
      { start: '2025-02-20T14:00:00', end: '2025-02-20T16:00:00' },
      { start: '2025-06-01T09:00:00', end: '2025-06-01T11:00:00' },
    ];

    setBookedEvents(mockData.map(event => ({
      ...event,
      start: new Date(event.start),
      end: new Date(event.end),
      title: 'Booked',
    })));
  }, []);

  // Handle clicking an available slot
  const handleSelectSlot = ({ start, end }) => {
    const startStr = moment(start).format('YYYY-MM-DD HH:mm');

    if (bookedEvents.some(event =>
      moment(start).isBetween(event.start, event.end, 'minute', '[)')
    )) {
      alert(`❌ Time slot ${startStr} is already booked!`);
      return;
    }

    alert(`✅ Booking request for ${startStr}`);
    navigate(`/booking/${moment(start).format('YYYY-MM-DD')}/${moment(start).format('HH:mm')}`);
  };

  // Handle clicking a booked event
  const handleSelectEvent = (event) => {
    alert(`⚠️ You clicked on a booked time slot: ${moment(event.start).format('YYYY-MM-DD HH:mm')} - ${moment(event.end).format('HH:mm')}`);
  };

  // Navigate between weeks
  const handleNavigate = (action) => {
    setCurrentDate((prevDate) =>
      action === 'prev'
        ? moment(prevDate).subtract(1, 'week').toDate()
        : moment(prevDate).add(1, 'week').toDate()
    );
  };

  // Style booked events
  const eventStyleGetter = () => ({
    style: {
      backgroundColor: 'red',
      color: 'white',
      borderRadius: '5px',
      textAlign: 'center',
    },
  });

  // Generate 2-hour time slots (8 AM to 10 AM, 10 AM to 12 PM, etc.)
  const timeSlots = useMemo(() => {
    const slots = [];
    const startHour = 8;
    const endHour = 18; // End at 6 PM
    for (let i = startHour; i < endHour; i += 2) {
      slots.push({
        start: moment().set({ hour: i, minute: 0, second: 0, millisecond: 0 }).toDate(),
        end: moment().set({ hour: i + 2, minute: 0, second: 0, millisecond: 0 }).toDate(),
      });
    }
    return slots;
  }, []);

  return (
    <div className="App container mt-5">
      <header className="text-center mb-4">
        <h1>Hourly Booking Calendar</h1>
      </header>

      {/* Week Navigation Buttons */}
      <div className="d-flex justify-content-between mb-4">
        <button className="btn btn-primary" onClick={() => handleNavigate('prev')}>Previous Week</button>
        <button className="btn btn-primary" onClick={() => handleNavigate('next')}>Next Week</button>
      </div>

      <div className="card shadow">
        <div className="card-body">
          <Calendar
            localizer={localizer}
            events={[...bookedEvents, ...timeSlots]} // Include both booked and available slots
            startAccessor="start"
            endAccessor="end"
            selectable
            onSelectSlot={handleSelectSlot}
            onSelectEvent={handleSelectEvent}
            date={currentDate}
            onNavigate={(date) => setCurrentDate(date)}
            defaultView="week"
            views={['week', 'day']}
            step={30} // 30-minute intervals for available slots
            timeslots={1} // 1 timeslot per hour (i.e., 2-hour blocks)
            style={{ height: '70vh', minHeight: '500px' }}
            className="border-0"
            eventPropGetter={eventStyleGetter}
          />
        </div>
      </div>

      <footer className="text-center mt-4 text-muted">
        <p className="mb-1">Hourly Booking System</p>
        <small>🟢 Available | 🔴 Booked</small>
      </footer>
    </div>
  );
}

function BookingForm() {
  const { date, time } = useParams();
  const navigate = useNavigate();
  const [startTime, setStartTime] = useState(time);
  const [endTime, setEndTime] = useState('');

  return (
    <div className="container mt-5">
      <div className="card shadow-lg">
        <div className="card-body">
          <h2 className="text-center mb-4">Booking Form</h2>
          <p className="text-center mb-4">You've selected: <strong>{date} at {startTime}</strong></p>

          <form>
            <div className="mb-3">
              <label className="form-label">Start Time</label>
              <input type="time" className="form-control" value={startTime} onChange={(e) => setStartTime(e.target.value)} required />
            </div>

            <div className="mb-3">
              <label className="form-label">End Time</label>
              <input type="time" className="form-control" value={endTime} onChange={(e) => setEndTime(e.target.value)} required />
            </div>

            <div className="mb-3">
              <label className="form-label">Your Name</label>
              <input type="text" className="form-control" placeholder="Enter your name" required />
            </div>

            <div className="mb-3">
              <label className="form-label">Email</label>
              <input type="email" className="form-control" placeholder="Enter your email" required />
            </div>

            <div className="d-flex justify-content-between">
              <button type="submit" className="btn btn-success">Submit Booking</button>
              <button type="button" className="btn btn-secondary" onClick={() => navigate('/')}>Back to Calendar</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

// Export Routes component with nested routes
function RoutesComponent() {
  return (
    <Routes>
      <Route path="/" element={<App />} />
      <Route path="/booking/:date/:time" element={<BookingForm />} />
    </Routes>
  );
}

export default RoutesComponent;
