import React, { useState, useEffect, useMemo } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import 'bootstrap/dist/css/bootstrap.min.css';

const localizer = momentLocalizer(moment);

function App() {
  const [bookedDates, setBookedDates] = useState([]);

  // Simulated API call - replace with real API
  useEffect(() => {
    const mockBookedDates = ['2025-02-15', '2025-02-20', '2025-06-01', '2025-06-10'];
    setBookedDates(mockBookedDates);
  }, []);

  // Convert booked dates into events for the calendar
  const events = useMemo(
    () =>
      bookedDates.map((date) => ({
        title: 'Booked',
        start: new Date(date),
        end: new Date(date),
        allDay: true,
      })),
    [bookedDates]
  );

  // Handle user selecting an available date
  const handleSelectSlot = ({ start }) => {
    const dateStr = moment(start).format('YYYY-MM-DD');

    if (bookedDates.includes(dateStr)) {
      alert(`❌ Date ${dateStr} is already booked!`);
      return;
    }

    // Replace with real booking logic
    alert(`✅ Booking request for ${dateStr}`);
  };

  // Handle clicking on an already booked event
  const handleSelectEvent = (event) => {
    alert(`⚠️ You clicked on a booked date: ${event.title}`);
  };

  // Style booked events
  const eventStyleGetter = (event) => ({
    style: {
      backgroundColor: 'red', // Mark booked dates in red
      color: 'white',
      borderRadius: '5px',
      textAlign: 'center',
    },
  });

  return (
    <div className="App container mt-5">
      <header className="text-center mb-4">
        <h1>Booking Calendar</h1>
      </header>

      <div className="card shadow">
        <div className="card-body">
          <Calendar
            localizer={localizer}
            events={events} // Display booked events
            startAccessor="start"
            endAccessor="end"
            selectable
            onSelectSlot={handleSelectSlot} // Handle clicking on an available date
            onSelectEvent={handleSelectEvent} // Handle clicking on booked dates
            defaultView="month"
            views={['month']}
            style={{ height: '70vh', minHeight: '500px' }}
            className="border-0"
            eventPropGetter={eventStyleGetter}
          />
        </div>
      </div>

      <footer className="text-center mt-4 text-muted">
        <p className="mb-1">Example Booking System</p>
        <small>🟢 Available | 🔴 Booked</small>
      </footer>
    </div>
  );
}

export default App;
