import React, { useState, useEffect } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import logo from './logo.svg';

const localizer = momentLocalizer(moment);

function App() {
  const [bookedDates, setBookedDates] = useState(new Set());

  // Mock API call - replace with real API
  useEffect(() => {
    const mockBookedDates = [
      '2024-05-15',
      '2024-05-20',
      '2024-06-01',
      '2024-06-10'
    ];
    setBookedDates(new Set(mockBookedDates));
  }, []);

  const dayPropGetter = (date) => {
    const dateStr = moment(date).format('YYYY-MM-DD');
    const isBooked = bookedDates.has(dateStr);
    return {
      className: isBooked ? 'bg-danger text-white' : 'bg-success text-white',
      style: {
        cursor: isBooked ? 'not-allowed' : 'pointer',
      },
    };
  };

  const handleSelectSlot = (slotInfo) => {
    const dateStr = moment(slotInfo.start).format('YYYY-MM-DD');
    if (!bookedDates.has(dateStr)) {
      // Replace with actual booking logic
      alert(`Would book ${dateStr}`);
    }
  };

  return (
    <div className="App container mt-5">
      <header className="mb-5">
        <img src={logo} className="App-logo img-fluid mb-3" alt="logo" />
        <h1 className="mb-4">Booking Calendar</h1>
      </header>

      <div className="card shadow">
        <div className="card-body">
          <Calendar
            localizer={localizer}
            events={[]}
            startAccessor="start"
            endAccessor="end"
            selectable
            onSelectSlot={handleSelectSlot}
            dayPropGetter={dayPropGetter}
            defaultView="month"
            views={['month']}
            style={{
              height: '70vh',
              minHeight: '500px'
            }}
            className="border-0"
          />
        </div>
      </div>

      <footer className="mt-5 py-4 text-muted">
        <p className="mb-1">Example booking system</p>
        <small>
          Green = Available, Red = Booked
        </small>
      </footer>
    </div>
  );
}

export default App;
