import React from "react";
import { Container } from "react-bootstrap";
import HeaderNavigation from "./HeaderNavigation";
import HeroSection from "./HeroSection";
import InfoSection from "./InfoSection";
import HowItWorks from "./HowItWorks";

const Layout = () => {
  return (
    <Container
      fluid
      className="d-flex flex-column min-vh-100"
      style={{
        background: "radial-gradient(circle, #ffffff, #f0f0f5)",
        backgroundAttachment: "fixed"
      }}
    >
      <HeaderNavigation />
      {/* Section one start*/}
      <div
        style={{
          background: "linear-gradient(120deg, #f8f9fa, #dde1e7)",
          padding: "60px 20px",
          borderRadius: "15px",
          margin: "30px",
          boxShadow: "0px 6px 15px rgba(0, 0, 0, 0.1)",
          textAlign: "center"
        }}
      >
        <HeroSection />
        <InfoSection />
      </div>
      {/* Section one end*/}
      {/* Section two start */}
      <div
        style={{
          background: "linear-gradient(120deg, #e3f2fd, #e1bee7)",
          padding: "60px 20px",
          borderRadius: "15px",
          margin: "30px",
          boxShadow: "0px 6px 15px rgba(0, 0, 0, 0.1)",
          textAlign: "center"
        }}
      >
        <HowItWorks />
      </div>
      {/* Section two end */}
    </Container>
  );
};

export default Layout;
