import React, { useState, useEffect, useMemo } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useNavigate } from 'react-router-dom';
import { Routes, Route } from 'react-router-dom';  
import { useParams } from 'react-router-dom';

const localizer = momentLocalizer(moment);

function App() {
  const [bookedDates, setBookedDates] = useState([]);
  const [currentDate, setCurrentDate] = useState(new Date());
  const navigate = useNavigate();

  // Simulate fetching booked dates
  useEffect(() => {
    const mockData = {
      '2025-02': ['2025-02-15', '2025-02-20'],
      '2025-06': ['2025-06-01', '2025-06-10'],
    };
    const monthKey = moment(currentDate).format('YYYY-MM');
    setBookedDates(mockData[monthKey] || []);
  }, [currentDate]);

  // Convert booked dates into events
  const events = useMemo(
    () =>
      bookedDates.map((date) => ({
        title: 'Booked',
        start: new Date(date),
        end: new Date(date),
        allDay: true,
      })),
    [bookedDates]
  );

  // Handle clicking an available date
  const handleSelectSlot = ({ start, end }) => {
    const dateStr = moment(start).format('YYYY-MM-DD');

    // Check if the date is before today or already booked
    if (moment(dateStr).isBefore(moment().startOf('day'))) {
      alert('❌ Cannot select past dates!');
      return;
    }

    if (bookedDates.includes(dateStr)) {
      alert(`❌ Date ${dateStr} is already booked!`);
      return;
    }

    alert(`✅ Booking request for ${dateStr}`);
    navigate(`/booking/${dateStr}`);
  };

  // Handle clicking a booked event
  const handleSelectEvent = (event) => {
    alert(`⚠️ You clicked on a booked date: ${event.title}`);
  };

  // Navigate months dynamically
  const handleNavigate = (action) => {
    setCurrentDate((prevDate) =>
      action === 'prev'
        ? moment(prevDate).subtract(1, 'months').toDate()
        : moment(prevDate).add(1, 'months').toDate()
    );
  };

  // Style booked events
  const eventStyleGetter = () => ({
    style: {
      backgroundColor: 'red',
      color: 'white',
      borderRadius: '5px',
      textAlign: 'center',
    },
  });

  return (
    <div className="App container mt-5">
      <header className="text-center mb-4">
        <h1>Booking Calendar</h1>
      </header>

      {/* Month Navigation Buttons */}
      <div className="d-flex justify-content-between mb-4">
        <button className="btn btn-primary" onClick={() => handleNavigate('prev')}>
          Previous Month
        </button>
        <button className="btn btn-primary" onClick={() => handleNavigate('next')}>
          Next Month
        </button>
      </div>

      <div className="card shadow">
        <div className="card-body">
          <Calendar
            localizer={localizer}
            events={events}
            startAccessor="start"
            endAccessor="end"
            selectable
            onSelectSlot={handleSelectSlot}
            onSelectEvent={handleSelectEvent}
            date={currentDate}
            onNavigate={(date) => setCurrentDate(date)}
            defaultView="month"
            views={['month']}
            style={{ height: '70vh', minHeight: '500px' }}
            className="border-0"
            eventPropGetter={eventStyleGetter}
            minDate={new Date()} // Disable past dates
          />
        </div>
      </div>

      <footer className="text-center mt-4 text-muted">
        <p className="mb-1">Example Booking System</p>
        <small>🟢 Available | 🔴 Booked</small>
      </footer>
    </div>
  );
}

function BookingForm() {
  const { date } = useParams();
  const navigate = useNavigate();

  return (
    <div className="container mt-5">
      <div className="card shadow-lg">
        <div className="card-body">
          <h2 className="text-center mb-4">Booking Form</h2>
          <p className="text-center mb-4">You've selected the date: <strong>{date}</strong></p>

          <form>
            <div className="mb-3">
              <label htmlFor="name" className="form-label">Your Name</label>
              <input 
                type="text" 
                className="form-control" 
                id="name" 
                placeholder="Enter your name" 
                required 
              />
            </div>

            <div className="mb-3">
              <label htmlFor="email" className="form-label">Email</label>
              <input 
                type="email" 
                className="form-control" 
                id="email" 
                placeholder="Enter your email" 
                required 
              />
            </div>

            <div className="mb-3">
              <label htmlFor="phone" className="form-label">Phone Number</label>
              <input 
                type="tel" 
                className="form-control" 
                id="phone" 
                placeholder="Enter your phone number" 
                required 
              />
            </div>

            <div className="mb-3">
              <label htmlFor="comments" className="form-label">Additional Comments</label>
              <textarea 
                className="form-control" 
                id="comments" 
                rows="3" 
                placeholder="Any special requests?" 
              ></textarea>
            </div>

            <div className="d-flex justify-content-between">
              <button type="submit" className="btn btn-success">Submit Booking</button>
              <button 
                type="button" 
                className="btn btn-secondary" 
                onClick={() => navigate('/')}
              >
                Back to Calendar
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

function RoutesComponent() {
  return (
    <Routes>
      <Route path="/" element={<App />} />
      <Route path="/booking/:date" element={<BookingForm />} />
    </Routes>
  );
}

export default RoutesComponent;
