import React, { useState, useEffect, useMemo } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useNavigate } from 'react-router-dom';
import { Routes, Route } from 'react-router-dom';
import { useParams } from 'react-router-dom';

const localizer = momentLocalizer(moment);

function App() {
  const [bookedEvents, setBookedEvents] = useState([]);
  const [currentDate, setCurrentDate] = useState(new Date());
  const navigate = useNavigate();

  // Simulated API call â€“ booked events (each is a 2-hour block)
  useEffect(() => {
    const mockData = [
      { start: '2025-02-15T10:00:00', end: '2025-02-15T12:00:00' },
      { start: '2025-02-20T14:00:00', end: '2025-02-20T16:00:00' },
      // Example booking on June 1st for 8â€“10 AM:
      { start: '2025-06-01T08:00:00', end: '2025-06-01T10:00:00' },
    ];

    setBookedEvents(
      mockData.map(event => ({
        ...event,
        start: new Date(event.start),
        end: new Date(event.end),
        title: 'Booked',
      }))
    );
  }, []);

  // Generate the 2â€‘hour available slots for the current day (from 8 AM to 8 PM)
  const timeSlots = useMemo(() => {
    const slots = [];
    const startHour = 8; // starting at 8 AM
    const endHour = 20;  // up to 8 PM
    // Use the currentDate (reset to midnight) so the slots fall on the correct day
    const baseDate = moment(currentDate).startOf('day');
    for (let hour = startHour; hour < endHour; hour += 2) {
      const slotStart = baseDate.clone().hour(hour).minute(0).second(0).toDate();
      const slotEnd = baseDate.clone().hour(hour + 2).minute(0).second(0).toDate();
      slots.push({
        start: slotStart,
        end: slotEnd,
        title: 'Available',
      });
    }
    return slots;
  }, [currentDate]);

  // Remove available slots that overlap with any booked event
  const availableTimeSlots = useMemo(() => {
    return timeSlots.filter(slot =>
      !bookedEvents.some(booked =>
        // Check if any edge of the slot falls inside an already booked event
        moment(slot.start).isBetween(booked.start, booked.end, 'minute', '[)') ||
        moment(slot.end).isBetween(booked.start, booked.end, 'minute', '[)') ||
        // Also, if a booked event starts during the available slot
        moment(booked.start).isBetween(slot.start, slot.end, 'minute', '[)')
      )
    );
  }, [timeSlots, bookedEvents]);

  // Merge booked events and available slots for rendering
  const allEvents = useMemo(() => {
    return [...bookedEvents, ...availableTimeSlots];
  }, [bookedEvents, availableTimeSlots]);

  // Style events: booked slots in red, available slots in green
  const eventStyleGetter = (event) => {
    if (event.title === 'Booked') {
      return {
        style: {
          backgroundColor: 'red',
          color: 'white',
          borderRadius: '5px',
          textAlign: 'center',
        },
      };
    } else if (event.title === 'Available') {
      return {
        style: {
          backgroundColor: 'green',
          color: 'white',
          borderRadius: '5px',
          textAlign: 'center',
        },
      };
    }
    return {};
  };

  // Handle clicking on an event (only available slots will trigger a booking)
  const handleSelectEvent = (event) => {
    if (event.title === 'Booked') {
      alert(`âš ï¸ This slot is already booked:\n${moment(event.start).format('YYYY-MM-DD HH:mm')} - ${moment(event.end).format('HH:mm')}`);
    } else if (event.title === 'Available') {
      // Prevent booking more than one slot per day
      const alreadyBooked = bookedEvents.some(booked =>
        moment(booked.start).isSame(moment(event.start), 'day')
      );
      if (alreadyBooked) {
        alert('âŒ You can only book one slot per day!');
        return;
      }
      const startStr = moment(event.start).format('YYYY-MM-DD HH:mm');
      const endStr = moment(event.end).format('YYYY-MM-DD HH:mm');
      alert(`âœ… Booking request for ${startStr} to ${endStr}`);
      navigate(`/booking/${moment(event.start).format('YYYY-MM-DD')}/${moment(event.start).format('HH:mm')}`);
    }
  };

  // Allow navigation between weeks (or days)
  const handleNavigate = (action) => {
    setCurrentDate(prevDate =>
      action === 'prev'
        ? moment(prevDate).subtract(1, 'week').toDate()
        : moment(prevDate).add(1, 'week').toDate()
    );
  };

  return (
    <div className="App container mt-5">
      <header className="text-center mb-4">
        <h1>Hourly Booking Calendar</h1>
      </header>

      <div className="d-flex justify-content-between mb-4">
        <button className="btn btn-primary" onClick={() => handleNavigate('prev')}>Previous Week</button>
        <button className="btn btn-primary" onClick={() => handleNavigate('next')}>Next Week</button>
      </div>

      <div className="card shadow">
        <div className="card-body">
          {/* By not setting the "selectable" prop, we disable freeform selection
              (so clicking on the day header or empty space does nothing) */}
          <Calendar
            localizer={localizer}
            events={allEvents}
            startAccessor="start"
            endAccessor="end"
            onSelectEvent={handleSelectEvent}
            date={currentDate}
            onNavigate={(date) => setCurrentDate(date)}
            defaultView="week"
            views={['week', 'day']}
            step={60}
            style={{ height: '70vh', minHeight: '500px' }}
            className="border-0"
            eventPropGetter={eventStyleGetter}
            min={moment(currentDate).hour(8).toDate()}
            max={moment(currentDate).hour(20).toDate()}
          />
        </div>
      </div>

      <footer className="text-center mt-4 text-muted">
        <p className="mb-1">Hourly Booking System</p>
        <small>ðŸŸ¢ Available | ðŸ”´ Booked</small>
      </footer>
    </div>
  );
}

function BookingForm() {
  const { date, time } = useParams();
  const navigate = useNavigate();
  const [startTime] = useState(time);

  return (
    <div className="container mt-5">
      <div className="card shadow-lg">
        <div className="card-body">
          <h2 className="text-center mb-4">Booking Form</h2>
          <p className="text-center mb-4">
            You've selected: <strong>{date} at {startTime}</strong>
          </p>

          <form>
            <div className="mb-3">
              <label className="form-label">Start Time</label>
              <input type="time" className="form-control" value={startTime} readOnly required />
            </div>

            <div className="mb-3">
              <label className="form-label">Your Name</label>
              <input type="text" className="form-control" placeholder="Enter your name" required />
            </div>

            <div className="mb-3">
              <label className="form-label">Email</label>
              <input type="email" className="form-control" placeholder="Enter your email" required />
            </div>

            <div className="d-flex justify-content-between">
              <button type="submit" className="btn btn-success">Submit Booking</button>
              <button type="button" className="btn btn-secondary" onClick={() => navigate('/')}>Back to Calendar</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

// Routes for navigation
function RoutesComponent() {
  return (
    <Routes>
      <Route path="/" element={<App />} />
      <Route path="/booking/:date/:time" element={<BookingForm />} />
    </Routes>
  );
}

export default RoutesComponent;
