import React, { useState, useEffect, useMemo } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter as Router, Route, Switch, useHistory } from 'react-router-dom';
import BookingForm from '/src/BookingForm'; // We'll create this component below

const localizer = momentLocalizer(moment);

function App() {
  const [bookedDates, setBookedDates] = useState([]);
  const [currentDate, setCurrentDate] = useState(new Date());
  const history = useHistory();

  // Simulated API call - Fetch booked dates for the selected month
  useEffect(() => {
    const mockData = {
      '2025-02': ['2025-02-15', '2025-02-20'],
      '2025-06': ['2025-06-01', '2025-06-10'],
    };
    const monthKey = moment(currentDate).format('YYYY-MM');
    setBookedDates(mockData[monthKey] || []);
  }, [currentDate]);

  // Convert booked dates into events
  const events = useMemo(
    () =>
      bookedDates.map((date) => ({
        title: 'Booked',
        start: new Date(date),
        end: new Date(date),
        allDay: true,
      })),
    [bookedDates]
  );

  // Handle clicking an available date
  const handleSelectSlot = ({ start, end }) => {
    const dateStr = moment(start).format('YYYY-MM-DD');

    if (bookedDates.includes(dateStr)) {
      alert(`âŒ Date ${dateStr} is already booked!`);
      return;
    }

    // Redirect to Booking Form with selected date
    history.push(`/booking/${dateStr}`);
  };

  // Handle clicking a booked event
  const handleSelectEvent = (event) => {
    alert(`âš ï¸ You clicked on a booked date: ${event.title}`);
  };

  // Navigate months dynamically
  const handleNavigate = (action) => {
    setCurrentDate((prevDate) =>
      action === 'prev'
        ? moment(prevDate).subtract(1, 'months').toDate()
        : moment(prevDate).add(1, 'months').toDate()
    );
  };

  // Style booked events
  const eventStyleGetter = () => ({
    style: {
      backgroundColor: '#f44336', // Red color for booked dates
      color: 'white',
      borderRadius: '8px',
      textAlign: 'center',
      padding: '5px',
      fontSize: '14px',
    },
  });

  return (
    <Router>
      <div className="App container mt-5">
        <header className="text-center mb-4">
          <h1 className="display-4 font-weight-bold text-primary">Booking Calendar</h1>
        </header>

        {/* Month Navigation Buttons */}
        <div className="d-flex justify-content-between align-items-center mb-3">
          <button
            className="btn btn-outline-primary"
            onClick={() => handleNavigate('prev')}
            style={{ transition: 'all 0.3s ease' }}
          >
            &#8592; Previous
          </button>
          <h4 className="font-weight-bold">{moment(currentDate).format('MMMM YYYY')}</h4>
          <button
            className="btn btn-outline-primary"
            onClick={() => handleNavigate('next')}
            style={{ transition: 'all 0.3s ease' }}
          >
            Next &#8594;
          </button>
        </div>

        <div className="card shadow-lg rounded-lg border-0">
          <div className="card-body">
            <Calendar
              localizer={localizer}
              events={events}
              startAccessor="start"
              endAccessor="end"
              selectable
              onSelectSlot={handleSelectSlot} // Trigger for available dates
              onSelectEvent={handleSelectEvent} // Trigger for booked dates
              date={currentDate}
              onNavigate={(date) => setCurrentDate(date)} // Allow built-in scrolling
              defaultView="month"
              views={['month']}
              style={{
                height: '70vh',
                minHeight: '500px',
                borderRadius: '8px',
                boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
              }}
              className="border-0"
              eventPropGetter={eventStyleGetter}
            />
          </div>
        </div>

        <footer className="text-center mt-4 text-muted">
          <p className="mb-1">Example Booking System</p>
          <small>ðŸŸ¢ Available | ðŸ”´ Booked</small>
        </footer>
      </div>

      <Switch>
        <Route path="/booking/:date" component={BookingForm} />
      </Switch>
    </Router>
  );
}

export default App;
